  /** Spanish overrides for every element: name, description, uses */
export interface EsElement { name: string; description: string; uses: string[] }

export const esTranslations: Record<number, EsElement> = {
  1:  { name: 'Hidrógeno',      description: 'El hidrógeno es el elemento más ligero y abundante del universo, constituyendo aproximadamente el 75 % de toda la materia bariónica. En condiciones normales es un gas diatómico incoloro e inodoro.', uses: ['Producción de amoniaco y fertilizantes mediante el proceso Haber-Bosch', 'Combustible de celdas de hidrógeno para vehículos eléctricos sin emisiones', 'Refinación de petróleo e hidrogenación de aceites vegetales'] },
  2:  { name: 'Helio',          description: 'El helio es el segundo elemento más abundante del universo. Es un gas noble incoloro, inodoro y no tóxico que se forma principalmente por desintegración radiactiva del uranio y el torio en la corteza terrestre.', uses: ['Enfriamiento de imanes superconductores en resonancia magnética (MRI)', 'Gas portador en cromatografía de gases de alta presión', 'Mezclas respirables para buzos en inmersiones profundas'] },
  3:  { name: 'Litio',          description: 'El litio es el metal alcalino más ligero y el sólido con menor densidad. Es altamente reactivo, blando y de color plateado. Se almacena en aceite mineral para prevenir su oxidación al contacto con el aire.', uses: ['Baterías recargables de iones de litio para electrónica y vehículos eléctricos', 'Tratamiento del trastorno bipolar y otras enfermedades psiquiátricas', 'Aleaciones de litio-aluminio para estructuras aeroespaciales ultraligeras'] },
  4:  { name: 'Berilio',        description: 'El berilio es un metal alcalinotérreo ligero, rígido y frágil de color gris acero. Tiene una combinación excepcional de ligereza, rigidez y punto de fusión elevado que lo hace valioso en aplicaciones de alta tecnología.', uses: ['Ventanas de rayos X en equipos de diagnóstico médico e industrial', 'Componentes estructurales en aeronaves supersónicas y satélites', 'Moderador de neutrones en reactores nucleares de investigación'] },
  5:  { name: 'Boro',           description: 'El boro es un metaloide con propiedades intermedias entre los metales y no metales. Es el único elemento no metálico del grupo 13; en estado puro es un sólido negro muy duro con alto punto de fusión.', uses: ['Manufactura de vidrio borosilicato resistente al calor (Pyrex)', 'Ácido bórico y bórax como antisépticos, fungicidas y conservantes', 'Blindaje de neutrones en plantas nucleares y tratamiento de tumores (BNCT)'] },
  6:  { name: 'Carbono',        description: 'El carbono es la base de toda química orgánica y de la vida tal como la conocemos. Existe en varias formas alotrópicas como el diamante, el grafito, el grafeno y los fullerenos, cada uno con propiedades radicalmente distintas.', uses: ['Combustibles fósiles como carbón, petróleo y gas natural para generación de energía', 'Fibra de carbono en estructuras aeroespaciales, deportivas y automotrices de alto rendimiento', 'Grafeno y nanotubos de carbono en electrónica y materiales del futuro'] },
  7:  { name: 'Nitrógeno',      description: 'El nitrógeno constituye el 78 % de la atmósfera terrestre. Es un gas diatómico incoloro e inodoro que en su forma molecular es bastante inerte debido al triple enlace que une sus átomos, uno de los más fuertes de la química.', uses: ['Producción de amoniaco para fertilizantes que alimentan a más del 40 % de la población mundial', 'Nitrógeno líquido para criogenia, conservación biológica y enfriamiento industrial', 'Atmósferas inertes en soldadura, envasado de alimentos y producción de semiconductores'] },
  8:  { name: 'Oxígeno',        description: 'El oxígeno es el elemento no metálico más abundante de la corteza terrestre y es esencial para la respiración de casi todos los organismos vivos. Es un gas diatómico altamente reactivo y oxidante en condiciones ambientales.', uses: ['Respiración asistida en medicina, aviación y exploración espacial', 'Corte y soldadura oxiacetilénica de metales en industria y construcción', 'Proceso básico de oxígeno (BOF) en la producción masiva de acero'] },
  9:  { name: 'Flúor',          description: 'El flúor es el elemento más electronegativo y reactivo de la tabla periódica. Es un gas diatómico amarillo pálido extremadamente corrosivo que puede atacar casi todos los materiales, incluyendo algunos metales nobles y el vidrio.', uses: ['Pasta dental y fluoración del agua potable para prevenir la caries dental', 'Fabricación de politetrafluoroetileno (PTFE/Teflón) para utensilios antiadherentes', 'Hexafluoruro de uranio en el enriquecimiento isotópico del uranio nuclear'] },
  10: { name: 'Neón',           description: 'El neón es un gas noble incoloro e inodoro presente en trazas en la atmósfera terrestre. Se obtiene industrialmente por destilación fraccionada del aire líquido y es conocido mundialmente por su uso en señalización luminosa.', uses: ['Tubos de descarga de gas para letreros y publicidad luminosa de color rojo-naranja', 'Láseres de neón-helio utilizados en experimentos ópticos y lectores de código de barras', 'Indicadores de alto voltaje y tubos de televisión de alta definición'] },
  11: { name: 'Sodio',          description: 'El sodio es un metal alcalino blando y plateado que reacciona vigorosamente con el agua liberando hidrógeno. Es el sexto elemento más abundante en la corteza terrestre y el principal catión del líquido extracelular en los organismos.', uses: ['Cloruro de sodio (sal de mesa) como condimento y conservante alimentario', 'Lámparas de vapor de sodio para alumbrado público de alta eficiencia energética', 'Sodio metálico como agente reductor en síntesis química industrial'] },
  12: { name: 'Magnesio',       description: 'El magnesio es el tercer metal más utilizado industrialmente. Es el elemento más ligero de uso estructural práctico. Sus aleaciones combinan baja densidad con buena resistencia mecánica y son ampliamente usadas en automoción y aeronáutica.', uses: ['Aleaciones magnesio-aluminio en componentes automotrices y aeroespaciales para reducción de peso', 'Clorofila vegetal: el magnesio es el átomo central en la molécula responsable de la fotosíntesis', 'Pirotecnia y bengalas militares gracias a su combustión con llama blanca muy brillante'] },
  13: { name: 'Aluminio',       description: 'El aluminio es el metal más abundante en la corteza terrestre y el tercer elemento más abundante tras el oxígeno y el silicio. Es ligero, resistente a la corrosión, buen conductor eléctrico y totalmente reciclable.', uses: ['Estructuras aeronáuticas y carrocerías de automóviles donde la ligereza es crítica', 'Envases de bebidas, papel de aluminio y embalaje en la industria alimentaria', 'Líneas de transmisión eléctrica de alta tensión por su relación resistividad/peso'] },
  14: { name: 'Silicio',        description: 'El silicio es el segundo elemento más abundante en la corteza terrestre tras el oxígeno. Es la base de la industria electrónica moderna: sus propiedades semiconductoras lo hacen indispensable para transistores, circuitos integrados y paneles solares.', uses: ['Chips semiconductores, microprocesadores y circuitos integrados en toda la electrónica digital', 'Paneles fotovoltaicos para generación de energía solar a escala residencial e industrial', 'Silicones, vidrio, hormigón y cerámica como materiales de construcción y sellado'] },
  15: { name: 'Fósforo',        description: 'El fósforo existe en varias formas alotrópicas, siendo las más comunes el blanco (muy reactivo y tóxico) y el rojo (más estable). Es esencial para la vida como componente del ADN, ARN, ATP y los fosfolípidos de las membranas celulares.', uses: ['Fertilizantes fosfatados que representan más del 80 % del consumo mundial de fósforo', 'Cabezas de cerillas de seguridad elaboradas con fósforo rojo', 'Aditivos retardantes de llama en plásticos, textiles y materiales de construcción'] },
  16: { name: 'Azufre',         description: 'El azufre es un no metal sólido de color amarillo brillante que se deposita naturalmente cerca de volcanes y fuentes termales. Es el noveno elemento más abundante en el universo y esencial para la síntesis de varios aminoácidos.', uses: ['Producción de ácido sulfúrico (H₂SO₄), el compuesto químico industrial más fabricado del mundo', 'Vulcanización del caucho natural para obtener elastómeros duraderos y resistentes', 'Fungicidas y pesticides agrícolas en forma de polvo o soluciones acuosas'] },
  17: { name: 'Cloro',          description: 'El cloro es un gas halógeno amarillo verdoso con fuerte olor irritante. Es uno de los productos químicos industriales más producidos mundialmente. Tiene potentes propiedades germicidas y blanqueantes que lo hacen esencial en higiene y saneamiento.', uses: ['Desinfección del agua potable y de piscinas para eliminar bacterias y microorganismos', 'Fabricación de PVC (policloruro de vinilo) para tuberías, cables y plásticos de construcción', 'Producción de blanqueadores domésticos, desinfectantes hospitalarios y agentes biocidas'] },
  18: { name: 'Argón',          description: 'El argón es el gas noble más abundante en la atmósfera terrestre, representando aproximadamente el 0,93 % del aire seco. Es completamente inerte en condiciones normales, por lo que se usa en ambientes donde se require inactividad química.', uses: ['Atmósfera protectora inerte en soldadura MIG/TIG de metales reactivos como titanio y aluminio', 'Relleno de bombillas incandescentes y lámparas de bajo consumo para prolongar la vida del filamento', 'Conservación de documentos históricos, obras de arte y materiales sensibles al oxígeno'] },
  19: { name: 'Potasio',        description: 'El potasio es un metal alcalino blando de color plateado que reacciona explosivamente con el agua. Es el séptimo elemento más abundante en la corteza terrestre y es vital para la función celular, especialmente en la transmisión de impulsos nerviosos.', uses: ['Fertilizantes potásicos (cloruro de potasio) esenciales para el crecimiento de cultivos agrícolas', 'Cloruro de potasio como sustituto de la sal y suplemento electrolítico en medicina deportiva', 'Soluciones cardioplégicas en cirugía a corazón abierto para detener temporalmente el corazón'] },
  20: { name: 'Calcio',         description: 'El calcio es el quinto elemento más abundante en la corteza terrestre y el metal alcalinotérreo más abundante. Es esencial para la estructura de huesos, dientes y conchas, y desempeña un papel crucial como mensajero celular en la señalización biológica.', uses: ['Construcción: cal viva y cal apagada en morteros, cementos y yesos desde la antigüedad', 'Suplementos de calcio y alimentos enriquecidos para prevenir la osteoporosis', 'Agente reductor en la producción metalúrgica de uranio, torio, circonio y otros metales'] },
  21: { name: 'Escandio',       description: 'El escandio es un metal de transición raro de aspecto plateado que se oxida lentamente en el aire. A pesar de ser relativamente escaso, sus aleaciones son muy valoradas en aplicaciones de alta tecnología donde la resistencia y la ligereza son cruciales.', uses: ['Aleaciones aluminio-escandio en palos de béisbol, bastones de trekking y marcos de bicicletas deportivas', 'Lámparas de halogenuros metálicos con escandio para iluminación de estadios deportivos', 'Electrolito en celdas de combustible de óxido sólido (SOFC) de alta temperatura'] },
  22: { name: 'Titanio',        description: 'El titanio es un metal de transición ligero, resistente y con excelente resistencia a la corrosión. Tiene la mejor relación resistencia-peso de todos los metales estructurales y es biocompatible, características que lo hacen insustituible en biomedicina y aeronáutica.', uses: ['Implantes médicos: prótesis articulares, tornillos óseos e implantes dentales por su biocompatibilidad', 'Estructuras de aeronaves militares y civiles donde la combinación ligereza-resistencia es crítica', 'Dióxido de titanio (TiO₂) como pigmento blanco en pinturas, plásticos y cosméticos'] },
  23: { name: 'Vanadio',        description: 'El vanadio es un metal de transición duro y de color gris acero. Sus compuestos adoptan varios estados de oxidación que les confieren colores llamativos. Es un catalizador industrial importante y sus aleaciones tienen propiedades mecánicas superiores.', uses: ['Aceros microaleados con vanadio para la construcción de rascacielos, puentes y herramientas de corte', 'Catalizador de pentóxido de vanadio (V₂O₅) en la producción de ácido sulfúrico por el proceso de contacto', 'Baterías de flujo de vanadio para almacenamiento estacionario de energía renovable a gran escala'] },
  24: { name: 'Cromo',          description: 'El cromo es un metal de transición duro y brillante famoso por su resistencia a la corrosión y su capacidad de pulirse con un acabado espejo. Es el elemento que da el color verde a las esmeraldas y el color rojo al rubí.', uses: ['Recubrimientos de cromo duro (galvanotecnia) en carrocerías de automóviles y acero inoxidable', 'Acero inoxidable: el cromo es su componente clave (mínimo 10,5 %) que le confiere resistencia a la corrosión', 'Pigmentos de óxido de cromo en pinturas verdes, tintas y colorantes refractarios'] },
  25: { name: 'Manganeso',      description: 'El manganeso es un metal de transición gris plateado esencial en la fabricación de acero. Aunque frágil en estado puro, sus aleaciones son extremadamente duras. Es también un micronutriente esencial para el metabolismo de los organismos vivos.', uses: ['Aleante prioritario en la producción de acero: mejora dureza, resistencia y maquinabilidad', 'Dióxido de manganeso como cátodo en baterías alcalinas y pilas secas de uso doméstico', 'Permanganato de potasio como potente oxidante y desinfectante en tratamiento de aguas'] },
  26: { name: 'Hierro',         description: 'El hierro es el metal más utilizado por la humanidad y el cuarto elemento más abundante en la corteza terrestre. Es el componente principal del núcleo terrestre y de la hemoglobina de la sangre, donde transporta oxígeno a los tejidos.', uses: ['Acero estructural para la construcción de edificios, puentes, maquinaria y automóviles', 'Fundición gris y fundición maleable en piezas de motor, tuberías y utensilios de cocina', 'Catalizador de hierro en la síntesis de amoniaco por el proceso Haber-Bosch a escala industrial'] },
  27: { name: 'Cobalto',        description: 'El cobalto es un metal ferromagnético de color azul metálico brillante. Ha sido utilizado durante milenios como pigmento azul en cerámica y vidrio. Sus aleaciones son conocidas por su extraordinaria resistencia a altas temperaturas y su dureza.', uses: ['Cátodo de cobalto en baterías de litio para vehículos eléctricos y dispositivos electrónicos', 'Superaleaciones de cobalto en turbinas de gas de aviación que operan a temperaturas extremas', 'Cobalto-60 como fuente radiactiva en radioterapia oncológica y esterilización médica'] },
  28: { name: 'Níquel',         description: 'El níquel es un metal de transición duro, maleable, dúctil y resistente a la corrosión. Es el quinto elemento más abundante en la Tierra en su totalidad, aunque escaso en la corteza. Es ferromagnético a temperatura ambiente.', uses: ['Aceros inoxidables austeníticos (serie 300) con níquel para uso en cocinas, hospitales e industria química', 'Superaleaciones de níquel en motores de turbina a reacción y reactores nucleares', 'Recubrimientos electrolíticos de níquel para protección anticorrosiva y propósitos decorativos'] },
  29: { name: 'Cobre',          description: 'El cobre es uno de los primeros metales utilizados por la humanidad, hace más de 10 000 años. Es el mejor conductor eléctrico entre los metales comunes, de ahí su posición central en la infraestructura eléctrica moderna y en la transición energética.', uses: ['Cables y conductores eléctricos en toda la infraestructura eléctronica y de telecomunicaciones', 'Tuberías de fontanería, radiadores, intercambiadores de calor y equipos de refrigeración', 'Aleaciones de bronce (Cu-Sn) y latón (Cu-Zn) para piezas mecánicas, monedas e instrumentos musicales'] },
  30: { name: 'Zinc',           description: 'El zinc es un metal de transición y el cuarto metal más consumido industrialmente tras el hierro, el aluminio y el cobre. Forma una capa de óxido protectora en el aire que lo hace resistente a la corrosión y permite su uso como revestimiento protector.', uses: ['Galvanizado de acero para protegerlo de la corrosión en estructuras, vallas y carrocerías', 'Aleaciones de latón (Cu-Zn) ampliamente usadas en grifería, cerraduras e instrumentos musicales de viento', 'Óxido de zinc en cremas solares, pinturas, cauchos y como semiconductor en electrónica'] },
  31: { name: 'Galio',          description: 'El galio es un metal blando de bajo punto de fusión (29,8 °C) que se funde en la mano. Es extraordinariamente útil en semiconductores compuestos como el arseniuro de galio, que supera en eficiencia al silicio en aplicaciones de alta frecuencia y LED.', uses: ['Semiconductores de GaAs y GaN en LED de luz azul/blanca, láseres de diodo y circuitos de alta frecuencia', 'Termómetros de alta temperatura donde el mercurio no puede usarse por razones de seguridad', 'Células fotovoltaicas de GaAs de alta eficiencia en satélites y paneles solares concentradores'] },
  32: { name: 'Germanio',       description: 'El germanio es un metaloide brillante de color gris plateado. Fue uno de los primeros materiales semiconductores utilizados en la electrónica moderna. Predichó por Mendeleev como "eka-silicio" antes de su descubrimiento en 1886.', uses: ['Fibras ópticas de dióxido de germanio-silicio para telecomunicaciones de alta velocidad', 'Transistores de germanio en electrónica de radiofrecuencia y detectores de infrarrojos', 'Células solares de triple unión de germanio-galio-arsénico para aplicaciones espaciales'] },
  33: { name: 'Arsénico',       description: 'El arsénico es un metaloide tóxico de color gris metálico que existe en varias formas alotrópicas. Aunque famoso como veneno, tiene usos legítimos en semiconductores, conservantes de madera y medicina veterinaria y humana.', uses: ['Arseniuro de galio (GaAs) en diodos emisores de luz, láseres y circuitos integrados de alta velocidad', 'Conservante CCA (cobre-cromo-arsénico) para madera de construcción en exteriores', 'Trióxido de arsénico en el tratamiento de la leucemia promielocítica aguda (LPA)'] },
  34: { name: 'Selenio',        description: 'El selenio es un no metal que exhibe propiedades fotoconductoras notables: su conductividad eléctrica aumenta drásticamente con la exposición a la luz. Es un micronutriente esencial con propiedades antioxidantes en los organismos vivos.', uses: ['Células fotoeléctricas y sensores de luz en cámaras digitales y fotocopiadoras (xerografía)', 'Decolorante del vidrio verde producido por impurezas de hierro en la producción de vidrio óptico', 'Suplemento nutricional antioxidante y componente de la enzima glutatión peroxidasa'] },
  35: { name: 'Bromo',          description: 'El bromo es el único no metal que es líquido a temperatura ambiente. Es un líquido marrón rojizo con vapores suffocantes muy irritantes. Se obtiene principalmente de salmueras minerales y del agua del mar.', uses: ['Retardantes de llama bromados en plásticos, espumas y textiles para electrónica y construcción', 'Bromuro de metilo como fumigante de suelos agrícolas para el control de plagas', 'Bromuro de plata (AgBr) en películas fotográficas de plata de emulsión tradicional'] },
  36: { name: 'Kriptón',        description: 'El kriptón es un gas noble incoloro, inodoro y relativamente inerte. Aunque cinco veces más denso que el aire, está presente en la atmósfera terrestre en cantidades mínimas. Su nombre proviene del griego "kryptos" (oculto).', uses: ['Lámparas incandescentes de kriptón para alumbrado fotográfico y proyectores de alta intensidad', 'Láseres de kriptón-flúor (KrF) en litografía ultravioleta profunda para fabricación de chips', 'Estándar de longitud: el metro fue definido en 1960 con la línea espectral del kriptón-86'] },
  37: { name: 'Rubidio',        description: 'El rubidio es un metal alcalino blando de color plateado que se inflama espontáneamente en el aire húmedo y reacciona violentamente con el agua. Fue el primer elemento descubierto mediante espectroscopía de emisión en 1861.', uses: ['Relojes atómicos de rubidio utilizados en GPS, telecomunicaciones y sincronización de redes', 'Células fotoeléctricas aprovechando el efecto fotoeléctrico del rubidio para detectar luz visible', 'Investigaciones de física cuántica y producción de condensados de Bose-Einstein'] },
  38: { name: 'Estroncio',      description: 'El estroncio es un metal alcalinotérreo de color plateado que reacciona con el agua y se oxida rápidamente en el aire formando una capa amarilla. Sus compuestos producen una llama carmesí brillante característica que se usa en pirotecnia.', uses: ['Pirotecnia y bengalas de señalización militares que producen llamaradas rojas o carmesíes brillantes', 'Pintura de titanato de estroncio en señales de tráfico fluorescentes de alta visibilidad', 'Estroncio-90 como fuente de calor en generadores termoeléctricos de radioisótopos (RTG) espaciales'] },
  39: { name: 'Itrio',          description: 'El itrio es un metal de transición raro de aspecto plateado que se incluye frecuentemente entre los lantánidos por sus propiedades similares. Es esencial para la producción de LEDs blancos de alta eficiencia y superconductores de alta temperatura.', uses: ['Fósforo de itrio-aluminio-granate (YAG) dopado con europio para LEDs blancos de alta eficiencia', 'Superconductor YBCO (itrio-bario-cobre-óxido) que opera por encima de la temperatura del nitrógeno líquido', 'Estabilizador de zirconia en recubrimientos cerámicos de barrera térmica para turbinas de gas'] },
  40: { name: 'Circonio',       description: 'El circonio es un metal de transición brillante, duro y resistente a la corrosión que es prácticamente transparente a los neutrones térmicos. Esta propiedad nuclear única lo hace valioso en la industria de la energía nuclear como revestimiento de barras de combustible.', uses: ['Vaina de combustible nuclear: tubos de aleación de circonio (Zircaloy) que contienen el uranio en reactores', 'Circonia estabilizada con itrio como electrolito en celdas de combustible de óxido sólido', 'Bisturís y cuchillos cerámicos de óxido de circonio por su extraordinaria dureza y baja reactividad'] },
  41: { name: 'Niobio',         description: 'El niobio, anteriormente llamado columbio, es un metal de transición suave, dúctil y de color gris plateado. Es uno de los materiales superconductores más importantes y un elemento de microaleación fundamental para la industria siderúrgica.', uses: ['Microaleante del acero en oleoductos, gasoductos y aceros de alta resistencia para construcción naval', 'Superconductor de niobio-titanio en imanes del Gran Colisionador de Hadrones (LHC) del CERN', 'Condensadores de niobio en dispositivos electrónicos portátiles como telefonía e informática'] },
  42: { name: 'Molibdeno',      description: 'El molibdeno es un metal de transición de color gris plateado con uno de los puntos de fusión más altos de todos los elementos. Es altamente resistente tanto a la corrosión ácida como a las altas temperaturas, propiedades que lo hacen útil como elemento de aleación en aceros especiales.', uses: ['Aceros aleados con molibdeno para herramientas de perforación petrolera y componentes de motores', 'Electrodos de molibdeno en hornos eléctricos de vidrio y en la producción de fibra óptica', 'Disulfuro de molibdeno (MoS₂) como lubricante en seco para vacío y altas temperaturas'] },
  43: { name: 'Tecnecio',       description: 'El tecnecio fue el primer elemento puramente sintético, producido artificialmente por Perrier y Segrè en 1937. Todos sus isótopos son radiactivos y no existe de forma natural en cantidades apreciables en la Tierra. Su isótopo más estable tiene una vida media de 4,2 millones de años.', uses: ['Tecnecio-99m como radiotrazador en medicina nuclear: el isótopo médico más utilizado del mundo en diagnóstico por imagen', 'Investigación de la química de los elementos radiactivos sintéticos del grupo 7', 'Protección catódica de aceros anticorrosivos en estudios de investigación de materiales'] },
  44: { name: 'Rutenio',        description: 'El rutenio es un metal de platino raro de color blanco plateado y con alta dureza. Tiene propiedades catalíticas excepcionales y es el menos reactivo químicamente de los metales del grupo del platino, lo que lo hace valioso en catálisis heterogénea.', uses: ['Catalizador de rutenio en la síntesis de Fischer-Tropsch para la producción de combustibles sintéticos', 'Puntas de bolígrafos de primera calidad y contactos eléctricos de alta resistencia al desgaste', 'Electrodos de óxido de rutenio-titanio (RuO₂/TiO₂) en producción electrolítica de cloro-sosa'] },
  45: { name: 'Rodio',          description: 'El rodio es uno de los metales más raros y costosos del mundo. Es extremadamente resistente a la corrosión y a la oxidación, incluso a altas temperaturas. Su principal aplicación es como componente del catalizador de tres vías en los tubos de escape de automóviles.', uses: ['Catalizador de tres vías en convertidores catalíticos de automóviles para reducir HC, CO y NOₓ', 'Recubrimiento reflectante de rodio en espejos de telescopios y reflectores de alta precisión', 'Termopares de platino-rodio para medir temperaturas de hasta 1900 °C en hornos industriales'] },
  46: { name: 'Paladio',        description: 'El paladio es un metal noble de color blanco plateado que puede absorber hasta 900 veces su propio volumen de hidrógeno. Esta propiedad única, junto con su actividad catalítica, lo sitúa en el centro de la tecnología de celdas de combustible de hidrógeno.', uses: ['Catalizador de paladio en síntesis orgánica: reacciones de acoplamiento cruzado premiadas con el Nobel 2010', 'Catalizadores en convertidores catalíticos de automóviles junto al platino y el rodio', 'Almacenamiento y purificación de hidrógeno en sistemas de energía limpia y celdas de combustible'] },
  47: { name: 'Plata',          description: 'La plata es el metal con la mayor conductividad eléctrica y térmica de todos los elementos. Ha sido usada por la humanidad durante más de 5000 años en joyería, monedas y utensilios. Sus propiedades antimicrobianas la hacen valiosa en medicina moderna.', uses: ['Joyería, orfebrería, cubertería de plata y adornos por su belleza y resistencia a la corrosión', 'Soldaduras de alta conductividad, contactos eléctricos y componentes electrónicos de precisión', 'Nanopartículas de plata en recubrimientos antibacterianos de ropa médica, textiles y superficies hospitalarias'] },
  48: { name: 'Cadmio',         description: 'El cadmio es un metal de transición blando, maleable y de color azul blanquecino. Es muy tóxico y su uso está cada vez más restringido por regulaciones ambientales. Sin embargo, tiene propiedades electroquímicas valiosas que aún lo hacen útil en ciertas baterías.', uses: ['Baterías recargables de níquel-cadmio (NiCd) para herramientas portátiles y electrónica profesional', 'Pigmentos de sulfuro de cadmio (amarillos, naranjas y rojos) en pinturas artísticas de alta calidad', 'Revestimientos anticorrosivos en piezas de aviación y electrónica militar sometidas a ambientes agresivos'] },
  49: { name: 'Indio',          description: 'El indio es un metal blando, brillante y de bajo punto de fusión. Se adhiere fácilmente al vidrio y otros metales. Su compuesto más importante, el óxido de indio-estaño (ITO), es el material conductor transparente estándar de la industria electrónica de displays.', uses: ['Óxido de indio-estaño (ITO) como electrodo transparente en pantallas LCD, OLED y contactos de células solares', 'Soldaduras de baja temperatura y sellados herméticos en equipos criogénicos y de vacío', 'Revestimientos antifricción en cojinetes y piezas de motores de automoción'] },
  50: { name: 'Estaño',         description: 'El estaño es un metal de color blanco plateado con un característico "grito del estaño" cuando se dobla (crujido por maclado). Es resistente a la corrosión y no es tóxico, características que lo han hecho esencial para recubrir metales y en la industria alimentaria.', uses: ['Hojalata: recubrimiento de estaño sobre acero para fabricar latas de conservas de alimentos y bebidas', 'Soldadura de estaño-plomo (o libre de plomo: Sn-Ag-Cu) en electrónica para unir componentes en circuitos', 'Bronce (aleación Cu-Sn) para esculturas monumentales, campanas, instrumentos musicales e hélices navales'] },
  51: { name: 'Antimonio',      description: 'El antimonio es un metaloide de color blanco azulado muy brillante. Es un mal conductor del calor y la electricidad. Sus compuestos ignífugos son ampliamente utilizados como retardantes de llama en plásticos, textiles y equipos electrónicos.', uses: ['Trióxido de antimonio como retardante de llama sinérgico en plásticos, goma, textiles y pinturas', 'Aleaciones de plomo-antimonio para las placas de batería de plomo-ácido de automóviles', 'Semiconductor de antimoniuro de indio (InSb) en detectores de infrarrojos y sensores de efecto Hall'] },
  52: { name: 'Telurio',        description: 'El telurio es un metaloide de color blanco plateado que es muy raro en la corteza terrestre. Su nombre derive de "tellus" (Tierra en latín). Es un semiconductor de tipo-p y tiene propiedades termoeléctricas y fotovoltaicas notables.', uses: ['Teluriduro de cadmio (CdTe) en películas delgadas para paneles solares fotovoltaicos de segunda generación', 'Agente de aleación en aceros de fácil mecanización para mejorar la maquinabilidad', 'Semiconductores termoelétricos de bismuto-telurio para módulos Peltier de enfriamiento electrónico'] },
  53: { name: 'Yodo',           description: 'El yodo es el único halógeno que es sólido a temperatura ambiente. Forma cristales violeta oscuro que subliman directamente a vapor. Es esencial para la síntesis de las hormonas tiroideas y su deficiencia causa bocio y cretinismo.', uses: ['Yoduro de potasio en sal yodada para prevenir la deficiencia de yodo a nivel mundial', 'Tintura de yodo y solución de Lugol como desinfectantes tópicos en heridas y cirugía', 'Yoduro de plata (AgI) para inducción artificial de lluvia mediante siembra de nubes'] },
  54: { name: 'Xenón',          description: 'El xenón es un gas noble denso, incoloro e inodoro que produce una intensa luz blanca azulada en las lámparas de descarga. Aunque inerte, puede formar compuestos con el flúor y el oxígeno bajo condiciones extremas. Tiene aplicaciones médicas como anestésico.', uses: ['Lámparas de xenón de alta intensidad en proyectores de cine, faros de automóvil y estroboscopios', 'Motor iónico de xenón para propulsión de satélites y naves espaciales mediante expulsión de iones', 'Anestésico general quirúrgico no tóxico con rápida recuperación del paciente'] },
  55: { name: 'Cesio',          description: 'El cesio es el metal alcalino con el punto de fusión más bajo (28,5 °C) y el elemento no radiactivo más electropositivo. Su transición hiperfina a 9 192 631 770 Hz define el segundo internacional en el Sistema Internacional de Unidades.', uses: ['Relojes atómicos de cesio: el estándar de frecuencia más preciso para GPS, telecomunicaciones e internet', 'Células fotoeléctricas e intensificadores de imagen en cámaras de visión nocturna y telescopios', 'Fluido de perforación en la industria petrolífera y geotérmica por su alta densidad'] },
  56: { name: 'Bario',          description: 'El bario es un metal alcalinotérreo blando de color plateado blancuzco. Es muy reactivo: reacciona con el agua, se oxida fácilmente en el aire y nunca se encuentra libre en la naturaleza. Sus compuestos insolubles son relativamente no tóxicos y útiles en medicina.', uses: ['Sulfato de bario como medio de contraste en radiografías del tracto gastrointestinal', 'Carbonato de bario en la producción de vidrio especial para pantallas de televisión y monitores', 'Óxido de bario en tubos de vacío y como captador de gases residuales (getter) en bulbos de vacío'] },
  57: { name: 'Lantano',        description: 'El lantano es el primer elemento de los lantánidos, una serie de 15 metales de tierras raras. Es un metal blando y maleable de color gris plateado que se oxida rápidamente en el aire. Da nombre a toda el grupo de los lantánidos.', uses: ['Óxido de lantano en cristales ópticos de alta índice de refracción para lentes fotográficas y telescopios', 'Hidruro de lantano-níquel en electrodos negativos de baterías de níquel-hidruro metálico (NiMH)', 'Catalizadores de craqueo catalítico en refinerías de petróleo para producción de gasolinas'] },
  58: { name: 'Cerio',          description: 'El cerio es el lantánido más abundante en la corteza terrestre y también es el elemento de las tierras raras más abundante. Es muy activo químicamente, con propiedades similares tanto al lantano como al praseodimio.', uses: ['Pulimento de óxido de cerio (CeO₂) para pulir vidrio óptico, lentes y pantallas de alta precisión', 'Catalizador de óxido de cerio en convertidores catalíticos para reducir emisiones de automóviles', 'Pedernales de cerio-hierro en encendedores y soldadores de gas'] },
  59: { name: 'Praseodimio',    description: 'El praseodimio es un lantánido blando, maleable y dúctil de color amarillo metálico. Su nombre deriva del griego "prasios" (verde puerro) y "didymos" (gemelo). Sus iones dan coloraciones verdes o amarillas a los vidrios y esmaltes.', uses: ['Imanes permanentes de aleación Nd-Fe-B dopados con praseodimio para reducir la dependencia del neodimio', 'Vidrios y esmaltes de colores verdes y amarillos para cerámica artística e industrial', 'Aleación mischmetal (con Ce, Nd y La) en electrodos negativos de baterías de NiMH'] },
  60: { name: 'Neodimio',       description: 'El neodimio es un lantánido de color plateado brillante que se usa principalmente para fabricar los imanes permanentes más potentes conocidos: los imanes de neodimio-hierro-boro. Un pequeño imán de neodimio puede soportar un peso miles de veces mayor que el suyo.', uses: ['Imanes permanentes Nd-Fe-B en motores de turbinas eólicas, discos duros y altavoces de alta fidelidad', 'Láseres Nd:YAG en cirugía, corte industrial, automotriz y en el LIDAR para vehículos autónomos', 'Colorante de vidrio y esmalte que produce colores desde azul-violeta hasta rojo vinoso'] },
  61: { name: 'Prometio',       description: 'El prometio es el único lantánido que no tiene isótopos estables en la naturaleza: todos sus isótopos son radiactivos. Recibió su nombre de Prometeo, el titán griego que robó el fuego a los dioses. Se produce artificialmente en reactores nucleares.', uses: ['Fuente de radiación beta en baterías nucleares y pinturas luminiscentes de instrumentos de aviación', 'Medición del grosor de materiales metálicos mediante atenuación de radiación beta', 'Investigación académica de las propiedades químicas de los elementos de las tierras raras'] },
  62: { name: 'Samario',        description: 'El samario es un lantánido duro, plateado y de aspecto brillante, con una de las primeras aplicaciones de las tierras raras en imanes permanentes. El compuesto samario-cobalto fue el primer imán permanente de tierras raras comercializado.', uses: ['Imanes permanentes de Sm-Co que mantienen su magnetismo a temperaturas superiores a 300 °C para motores de cohetes', 'Samario-153 como radionúclido terapéutico para el tratamiento paliativo de los dolores en metástasis óseas', 'Reactores nucleares: el samario es uno de los "venenos nucleares" más efectivos para controlar la fisión'] },
  63: { name: 'Europio',        description: 'El europio es el lantánido más reactivo y uno de los elementos de tierras raras más escasos. Destaca por sus extraordinarias propiedades de luminiscencia, que hacen que sus compuestos sean los fósforos más eficientes conocidos cuando se exponen a la luz ultravioleta.', uses: ['Fósforos de europio que generan el color rojo en televisiones de tubo catódico, LED blancos y lámparas fluorescentes', 'Fósforos de seguridad en billetes bancarios y documentos de identidad difíciles de falsificar', 'Investigación del espín nuclear y de la conexión espín-órbita en física teórica'] },
  64: { name: 'Gadolinio',      description: 'El gadolinio es el único lantánido ferromagnético a temperatura subnormal. Tiene la mayor sección eficaz de captura de neutrones térmicos de todos los elementos naturales, lo que lo hace muy valioso en aplicaciones nucleares y de imagen médica.', uses: ['Agente de contraste de gadolinio en resonancia magnética (MRI) para mejorar la visualización de tumores y tejidos', 'Absorsor de neutrones en barras de control de reactores nucleares y blindajes de neutrones', 'Refrigeración magnética cercana a la temperatura ambiente en sistemas criogénicos de nueva generación'] },
  65: { name: 'Terbio',         description: 'El terbio es un lantánido raro de color gris plateado que tiene una notable magnetostricción positiva: cambia de tamaño cuando se le aplica un campo magnético. Se le aísla por primera vez en 1843 de la tierra mineral gadolinita de Ytterby, Suecia.', uses: ['Aleación de magnetostricción Terfenol-D (Tb-Fe-Dy) para sensores de vibración acústica submarina y actuadores de ultrasonidos', 'Fósforo verde de terbio en pantallas de alta definición, tubos de rayos catódicos y LEDs de luz blanca', 'Películas de almacenamiento magneto-óptico de lectura/escritura en CD-ROM y discos ópticos de datos'] },
  66: { name: 'Disprosio',      description: 'El disprosio es un lantánido de color plateado metálico que es altamente paramagnético. Se usa principalmente para mejorar la coercitividad de los imanes de neodimio-hierro-boro, evitando que se desmagneticen a altas temperaturas en motores de vehículos eléctricos.', uses: ['Aditivo en imanes Nd-Fe-B para motores de vehículos eléctricos que mantiene su magnetismo a temperaturas operativas', 'Aleaciones de Terfenol-D junto con el terbio en transductores de sonar y actuadores de alta potencia', 'Fuente de radiación de disprosio-165 en cirugía de sinovectomía por radiación intraarticular'] },
  67: { name: 'Holmio',         description: 'El holmio es un lantánido con las propiedades magnéticas más fuertes de todos los elementos naturales. Su nombre proviene de "Holmia", el nombre latino de Estocolmo. Tiene una gran sección transversal de absorción de neutrones.', uses: ['Concentrador de campo magnético en imanes de alta intensidad para espectroscopía de RMN y MRI', 'Láseres de holmio (Ho:YAG) en cirugía urológica, ortopédica y oftalmológica de alta precisión', 'Colorante de calabaza para vidrios y esmaltes amarillos a rojos, y estándares de calibración óptica'] },
  68: { name: 'Erbio',          description: 'El erbio es un lantánido de color rosa metálico que tiene propiedades fluorescentes muy útiles. Su ión Er³⁺ emite luz infrarroja a 1550 nm, exactamente en la ventana de mínima atenuación de las fibras ópticas, lo que lo hace fundamental para las telecomunicaciones.', uses: ['Amplificadores de fibra óptica dopados con erbio (EDFA) en redes de telecomunicaciones de larga distancia', 'Láseres Er:YAG a 2940 nm para ablación de tejidos duros en odontología y dermatología estética', 'Colorante rosa para vidrios artísticos, esmaltes cerámicos y gafas de vidrio para infrarrojo cercano'] },
  69: { name: 'Tulio',          description: 'El tulio es el lantánido de menor abundancia natural y el de mayor precio entre las tierras raras. A pasar de ser raro y caro, tiene importantes aplicaciones médicas gracias a la emisión de rayos X de su isótopo Tm-170 producido en reactores nucleares.', uses: ['Tulio-170 en unidades portátiles de rayos X para su uso en campo, diagnóstico de campo y análisis no destructivo', 'Láseres de Tm:YAG a 2.01 μm para cirugía de tejidos blandos con mínima hemorragia', 'Fósforo de tulio en lámparas de mercurio de baja presión y lámparas UV de alta eficiencia'] },
  70: { name: 'Iterbio',        description: 'El iterbio es un lantánido blando y maleable de características similares a las de los demás lantánidos. Recibe su nombre del municipio sueco de Ytterby, del que también derivan sus nombres el itrio, el erbio y el terbio.', uses: ['Amplificadores de fibra óptica dopada con iterbio (YDFA) en sistemas láser de alta potencia de telecomunicaciones', 'Relojes atómicos ópticos de iterbio de la siguiente generación con precisión mejor de 10⁻¹⁸', 'Acero inoxidable y aleaciones especiales con iterbio para mejorar las propiedades mecánicas'] },
  71: { name: 'Lutecio',        description: 'El lutecio es el último de los lantánidos y uno de los el más denso y más duro de la serie de lantánidos. El Lu-177 es un radionúclido de especial importancia en medicina nuclear de precisión para radioterapia dirigida de tumores neuroendocrinos.', uses: ['Lu-177 en terapia de radionúclidos dirigida a receptores (Lu-PSMA) para cáncer de próstata avanzado', 'Oxiortosilicato de lutecio-cerio (LYSO:Ce) como cristal centelleador en PET para diagnóstico oncológico', 'Catalizadores de polímeros resistentes al calor y catalizadores de craqueo en refinerías petroleras'] },
  72: { name: 'Hafnio',         description: 'El hafnio siempre se encuentra junto al circonio en la naturaleza y son las minerales más difíciles de separar debido a sus propiedades casi idénticas. Sin embargo, sus propiedades nucleares difieren enormemente: el hafnio absorbe neutrones cientos de veces más que el circonio.', uses: ['Barras de control para reactores nucleares navales de alta potencia gracias a su absorción de neutrones', 'Dieléctrico de alta-κ (HfO₂) en transistores de última generación menores de 45 nm', 'Electrodos de plasma en corte y soldadura por plasma de alta potencia'] },
  73: { name: 'Tántalo',        description: 'El tántalo es un metal de transición duro, azul-gris y muy denso con uno de los puntos de fusión más altos de todos los elementos. Es prácticamente insoluble en ácidos a temperatura ambiente, lo que lo hace extraordinariamente resistente a la corrosión química.', uses: ['Condensadores electrolíticos de tántalo en teléfonos móviles, ordenadores portátiles y electrónica miniaturizada', 'Prótesis médicas e implantes quirúrgicos por su total biocompatibilidad con el tejido vivo humano', 'Equipos de reacción química resistentes a los ácidos más corrosivos en industria química y farmacéutica'] },
  74: { name: 'Tungsteno',      description: 'El tungsteno tiene el punto de fusión más alto de todos los elementos puros (3422 °C) y también la mayor resistencia a la tensión de todos los metales. Su nombre proviene del sueco "tung sten" (piedra pesada) y su símbolo W del alemán "Wolfram".', uses: ['Filamentos de bombillas incandescentes y lámparas halógenas de alta temperatura y alta luminosidad', 'Carburo de tungsteno en herramientas de corte, brocas de perforación y discos de corte industrial', 'Blindaje de radiación en equipos de radioterapia médica y contenedores de materiales radiactivos'] },
  75: { name: 'Renio',          description: 'El renio fue el último elemento estable no radiactivo descubierto en 1925. Tiene el segundo punto de fusión más alto de todos los elementos y uno de los mayores coeficientes de resistividad eléctrica entre los metales. Se obtiene principalmente como subproducto del refinado del molibdeno.', uses: ['Superaleaciones de níquel-renio para álabes de turbinas de reactores de avión de alta temperatura', 'Catalizadores de platino-renio en la reformación catalítica de la nafta para producción de gasolina de alta octanaje', 'Alambres de termopar de renio para temperaturas extremas hasta 2200 °C en entornos industriales'] },
  76: { name: 'Osmio',          description: 'El osmio es el elemento más denso de la tabla periódica (22,59 g/cm³). Es duro, frágil y tiene el punto de fusión más alto del grupo del platino. Su tetróxido es muy tóxico pero se usa en microscopía electrónica para contrastar muestras biológicas.', uses: ['Tetróxido de osmio (OsO₄) como agente de tinción de lípidos en preparación de muestras para microscopía electrónica', 'Punta de los bolígrafos estilográficos de primera calidad para extrema resistencia al desgaste', 'Catalizador en síntesis asimétrica de dihidrodioles (dihidroxilación de Sharpless) en química farmacéutica'] },
  77: { name: 'Iridio',         description: 'El iridio es el segundo elemento más denso y el metal más resistente a la corrosión conocido. Junto con el osmio, el platino y el renio, era utilizado en la definición del kilogramo hasta 2019. Su nombre proviene de Iris, diosa del arcoíris, por los colores de sus sales.', uses: ['Electrodos de bujías de encendido para vehículos de alto rendimiento y aviación por su dureza extrema', 'Fuente de Ir-192 para braquiterapia en tratamiento de cáncer de pulmón, mama y de próstata', 'Crisoles de iridio para el crecimiento de cristales de óxidos a temperatura muy elevada (> 2000 °C)'] },
  78: { name: 'Platino',        description: 'El platino es un metal noble denso, maleable y altamente resistente a la corrosión y a la oxidación. Fue usado como material de referencia en el kilogramo patrón hasta 2019. Es el catalizador más versátil y cuya demanda es mayor en la industria química moderna y de automoción.', uses: ['Catalizadores de platino en convertidores catalíticos de automóviles para reducir contaminantes', 'Joyería de platino de alta gama: anillos de matrimonio, broches y monturas por su belleza y durabilidad', 'Electrodos de platino en celdas de combustible de hidrógeno y pilas de combustible PEM'] },
  79: { name: 'Oro',            description: 'El oro ha fascinado a la humanidad durante toda su historia registrada. Es uno de los pocos elementos que se encuentra en forma nativa en la naturaleza. Sea brillante, inerte, maleable e impermeable a la corrosión lo han convertido en el estándar monetario universal por milenios.', uses: ['Joyería, monedas y lingotes como depósito de valor y reserva monetaria internacional', 'Contactos eléctricos de alta fiabilidad en circuitos electrónicos, conectores y chips de alta gama', 'Nanopartículas de oro en medicina oncológica para diagnóstico y terapia fototérmica de tumores'] },
  80: { name: 'Mercurio',       description: 'El mercurio es el único metal que es líquido a temperatura ambiente. Es muy tóxico para el sistema nervioso, hígado y riñones. Su nombre proviene del planeta Mercurio, mensajero de los dioses en la mitología romana. Su uso está cada vez más restringido por razones medioambientales.', uses: ['Termómetros, barómetros y manómetros de mercurio en laboratorios de alta precisión hasta 2020', 'Lámparas de vapor de mercurio y lámparas fluorescentes compactas (CFL) para iluminación urbana e industrial', 'Amalgamas dentales de mercurio-plata para restauraciones dentales de larga duración (uso en declive)'] },
  81: { name: 'Talio',          description: 'El talio es un metal blando, maleable y de color gris plateado que se parece físicamente al plomo. Es altamente tóxico y sus sales son inodoras e insípidas, razón por la que fue utilizado históricamente como veneno criminal hasta que su venta se restringió.', uses: ['Detectores de radiación gamma de haluro de talio (TlBr y TlI) en física de partículas', 'Escáner de talio-201 en cardiología nuclear para diagnóstico de perfusión miocárdica (isquemia)', 'Vidrios ópticos de alta densidad y alta índice de refracción para instrumentación especializada'] },
  82: { name: 'Plomo',          description: 'El plomo es un metal pesado blando y maleable de color gris azulado. Es altamente tóxico y se acumula en los tejidos. Ha sido usado por la humanidad desde la antigüedad pero su uso ha disminuido drásticamente en las últimas décadas por sus efectos nocivos neurotóxicos, especialmente en niños.', uses: ['Baterías de plomo-ácido en automóviles y sistemas de alimentación interrumpida (UPS) para arranque y reserva', 'Blindaje de radiación: el plomo absorbe efectivamente los rayos X y gamma en hospitales y plantas nucleares', 'Soldadura de plomo-estaño en electrónica tradicional y en plomería de fontanería antigua'] },
  83: { name: 'Bismuto',        description: 'El bismuto es el elemento con número atómico más alto que aún es prácticamente estable (aunque su isótopo más abundante es técnicamente radiactivo con vida media > 10¹⁹ años). Es el elemento diamagnético con mayor efecto magnetorresistivo conocido.', uses: ['Subsalicilato de bismuto como antiácido y antidiarreico en medicamentos gastrointestinales', 'Sustituto no tóxico del plomo en soldaduras, fusibles, munición de perdigones y materiales de construcción', 'Telururo de bismuto en módulos termoeléctricos Peltier para enfriamiento electrónico sin partes móviles'] },
  84: { name: 'Polonio',        description: 'El polonio fue descubierto en 1898 por Marie Curie y Pierre Curie y nombrado en honor de Polonia, patria de Marie. Es un elemento radiactivo con unas de las mayor actividad específica de todos los elementos naturales. Es 250 000 veces más tóxico que el cianuro de hidrógeno.', uses: ['Eliminadores de electricidad estática en maquinaria papelera y textil (fuente alfa de Po-210)', 'Fuente de calor por desintegración radiactiva en unidades de inicio de neutrones en armas nucleares (histórico)', 'Investigación de la física nuclear y del proceso de desintegración alfa en experimentos académicos'] },
  85: { name: 'Ástato',         description: 'El ástato es el halógeno más pesado y uno de los elementos naturales más raros: se estima que en cualquier momento hay menos de 30 gramos de ástato en toda la corteza terrestre. Su nombre proviene del griego "astatos" (inestable).', uses: ['At-211 en radioterapia alfa dirigida (TAT) para células cancerígenas resistentes a otros tratamientos', 'Investigación de la química de los halogenuros más pesados y de la relatividad en la química de las tierras raras', 'Estudio de las propiedades de los halógenos radiactivos en física y química nuclear experimental'] },
  86: { name: 'Radón',          description: 'El radón es un gas noble radiactivo que se forma continuamente por desintegración del radio y del uranio en minerales de la corteza terrestre. Es incoloro, inodoro e insípido. A niveles altos en interiores es la segunda causa de cáncer de pulmón después del tabaco.', uses: ['Radón-222 histórico en radioterapia de tumores superficiales (braquiterapia) en el siglo XX', 'Predictor de terremotos e indicador geofísico: sus emanaciones preceden actividad sísmica y volcánica', 'Investigación de los gases nobles pesados y de la química de compuestos de gases nobles de alta energía'] },
  87: { name: 'Francio',        description: 'El francio es el elemento natural más inestable y el segundo elemento natural más escaso después del ástato. Es el metal alcalino más pesado descubierto y recibe su nombre de Francia. En equilibrio hay menos de 30 gramos de francio en la corteza terrestre en todo momento.', uses: ['Investigación fundamental de la estructura atómica y prueba de las predicciones de la relatividad especial', 'Estudios de la simetría de violación en física de partículas mediante espectroscopía atómica de alta precisión', 'Referencia teórica para la extrapolación de las propiedades químicas de los metales alcalinos más pesados'] },
  88: { name: 'Radio',          description: 'El radio, descubierto por los esposos Curie en 1898, fue el primer elemento cuya radiactividad intensa fue conocida por el público general. Marie Curie fue la primera persona en ganar dos premios Nobel. El radio fue muy usado en medicina y pinturas luminiscentes hasta mediados del siglo XX.', uses: ['Radio-223 (Xofigo) en terapia de metástasis óseas de cáncer de próstata resistente a la castración', 'Pinturas luminiscentes históricas de relojes y cuadrantes marinos (hoy sustituidas por fósforos seguros)', 'Fuente de neutrones: el radio mezclado con berilio produce neutrones por reacción α-n para investigación nuclear'] },
  89: { name: 'Actinio',        description: 'El actinio fue el primer elemento radiactivo no uranio ni torio descubierto (1899). Da nombre a la serie de los actínidos. Es tan escaso que la producción anual mundial es de escasos microgramos en reactores nucleares como subproducto del radium.', uses: ['Actinio-225 como generador de bismuto-213 para radioterapia dirigida de leucemia y tumores sólidos', 'Investigación de las propiedades químicas de los actínidos y de la física nuclear de los transuránidos', 'Fuente de neutrones junto al berilio para instrumentación de medicion de pozos petrolíferos (actinio-Be)'] },
  90: { name: 'Torio',          description: 'El torio es un metal actínido radiactivo de color gris plateado. Es aproximadamente tres veces más abundante que el uranio en la corteza terrestre. El torio puede utilizarse como combustible nuclear en reactores de sal fundida con ventajas sobre el uranio en seguridad y eficiencia.', uses: ['Combustible nuclear en reactores de torio-ciclo de ciclo cerrado y reactores de sal de torio (TSR)', 'Torio-tungsteno en electrodos de soldadura TIG para mejorar la estabilidad del arco eléctrico', 'Incandescencias de gas (mantas de gas de torio-cerio) para alumbrado portátil de alta luminosidad'] },
  91: { name: 'Protactinio',    description: 'El protactinio es un actínido radiactivo y uno de los elementos naturales más escasos. Es el precursor del actinio en la cadena de desintegración del uranio-235. Tiene escasas aplicaciones prácticas fuera de la investigación nuclear por su alta radioactividad y escasez.', uses: ['Datación de sedimentos oceánicos profundos mediante la relación Pa-231/Th-230 (geocronología marina)', 'Investigación de las propiedades de los actínidos y de la fisión nuclear inducida por neutrones', 'Producción académica para estudiar la química de los actínidos en química de coordinación'] },
  92: { name: 'Uranio',         description: 'El uranio es el actínido más pesado que existe de forma natural en la corteza terrestre en cantidades apreciables. Es la fuente de energía de los reactores nucleares mediante la fisión del U-235. Fue descubierto en 1789 y es el elemento con mayor número atómico encontrado en la naturaleza comprobado.', uses: ['Combustible nuclear enriquecido en U-235 para reactores de agua a presión (PWR) y ebullición (BWR)', 'Uranio empobrecido en blindajes de tanques, proyectiles perforantes de alta densidad y contrapesos de aeronaves', 'Dióxido de uranio en combustibles nucleares MOX combinados con plutonio para reciclar material fisible'] },
  93: { name: 'Neptunio',       description: 'El neptunio fue el primer elemento transuránico sintetizado artificialmente en 1940. Se produce en pequeñas cantidades como subproducto del funcionamiento de reactores nucleares. Recibe su nombre del planeta Neptuno, siguiendo la secuencia uranio-Urano y neptunio-Neptuno.', uses: ['Producción de plutonio-238 a partir de Np-237 para generadores termoeléctricos de radioisótopos (RTG) en naves espaciales', 'Detectores de neutrones rápidos usando la reacción de fisión del Np-237 inducida por neutrones', 'Investigación de la química de los actínidos y de las propiedades de los elementos transuránidos'] },
  94: { name: 'Plutonio',       description: 'El plutonio es el actínido más conocido por su papel en las armas nucleares y en los reactores nucleares. El Pu-239 es fisible y fue el material del núcleo de la bomba lanzada sobre Nagasaki en 1945. El Pu-238 es un potente emisor de calor por desintegración alfa usado en RTG espaciales.', uses: ['Pu-238 en generadores termoeléctricos de radioisótopos (RTG) que alimentaron Pioneer, Voyager y Cassini', 'Pu-239 como combustible fisible en reactores de neutrones rápidos y en armas nucleares (históricamente)', 'Combustibles MOX (mezcla de óxidos de Pu y U) para reducir el inventario de plutonio militar de armas'] },
  95: { name: 'Americio',       description: 'El americio fue sintetizado por Glenn Seaborg en 1944 y recibe su nombre del continente americano. Es bien conocido por su uso cotidiano en detectores de humo. El Am-241 emite partículas alfa de alta energía que ionizan el aire en la cámara del detector.', uses: ['Detectores de humo iónicos domésticos de Am-241 presentes en millones de hogares en todo el mundo', 'Am-241 como fuente portátil de rayos X blandos en análisis de fluorescencia de rayos X (XRF)', 'Investigación nuclear de actínidos y producción de elementos de número atómico aún mayor (californio, etc.)'] },
  96: { name: 'Curio',          description: 'El curio fue sintetizado en 1944 y nombrado en honor a Marie y Pierre Curie. Es un actínido plateado muy radiactivo que produce suficiente calor por su desintegración para utilizarse como fuente termoeléctrica en el espacio. Es 3000 veces más radiactivo que el radio.', uses: ['Cm-244 en generadores quimiotérmicos para fuentes de energía en exploradores de planetas (alternativa al Pu-238)', 'Fuente de neutrones Cm-244/Be para registros de neutrones en pozos de petróleo y gas', 'Investigación de la fisión nuclear y de las propiedades de los actínidos transuránidos más pesados'] },
  97: { name: 'Berkelio',       description: 'El berkelio fue sintetizado por primera vez en 1949 en la Universidad de California, Berkeley, de la que toma nombre. Es producido en muy pequeñas cantidades por bombardeo de americio o curio con neutrones en reactores de alta fluencia de neutrones como el HFIR en Oak Ridge.', uses: ['Berkelio-249 como blanco de bombardeo para sintetizar elementos superpesados como el tenneso (117) y el oganesón (118)', 'Investigación de las propiedades nucleares y de la química de los actínidos en el extremo de la tabla periódica', 'Producto de laboratorio para estudios de separación y purificación radioquímica de actínidos'] },
  98: { name: 'Californio',     description: 'El californio fue sintetizado en 1950 en la Universidad de California, de la que toma su nombre. El Cf-252 es uno de los pocos isótopos que producen neutrones en abundancia por fisión espontánea y tiene aplicaciones directas en varios campos industriales y médicos.', uses: ['Cf-252 como fuente de neutrones portátil para análisis de activación neutrónica en minería y arqueología', 'Inicio de ciclos en reactores nucleares de nueva puesta en marcha mediante fuente de neutrones de arranque', 'Braquiterapia de tumores de cuello uterino y cabeza/cuello con fuentes de neutrones rápidos de Cf-252'] },
  99: { name: 'Einstenio',      description: 'El einstenio fue descubierto en 1952 en los residuos de la primera detonación de una bomba de hidrógeno termonuclear ("Ivy Mike") y fue nombrado en honor a Albert Einstein. Se produce en cantidades muy pequeñas y solo tiene usos en investigación fundamental avanzada.', uses: ['Síntesis del mendelevio-256 por bombardeo de einstenio-253 con partículas alfa en ciclotrón (primera síntesis artificial de un nuevo elemento)', 'Investigación de las propiedades químicas y electrónicas de los actínidos pesados sintéticos', 'Estudio académico de los niveles de energía atómica en actínidos mediante espectroscopía de alta resolución'] },
  100:{ name: 'Fermio',         description: 'El fermio fue descubierto en 1952 simultáneamente con el einstenio como producto de la detonación nuclear de "Ivy Mike". Fue nombrado en honor al físico italiano Enrico Fermi, diseñador del primer reactor nuclear. Su producción requiere irradiación neutrónica prolongada en reactores especializados.', uses: ['Investigación de los actínidos más pesados y límites de estabilidad nuclear en física de la materia nuclear', 'Síntesis de elementos más pesados por fusión nuclear de iones pesados en aceleradores especializados', 'Estudio del contorno de la "isla de estabilidad" hipotética de los elementos supra-pesados en física nuclear teórica'] },
  101:{ name: 'Mendelevio',     description: 'El mendelevio fue sintetizado por primera vez en 1955 y nombrado en honor del químico ruso Dmitri Mendeléyev, creador de la tabla periódica de los elementos. Es el primer elemento sintetizado en cantidades menores de un átomo a la vez, demostrando que la química se puede hacer con átomos individuales.', uses: ['Investigación de la estabilidad nuclear de los actínidos pesados y predicción de la "isla de estabilidad"', 'Estudio de la química de los actínidos más pesados y su similitud con los lantánidos', 'Producción de elementos transmendelevio de número atómico mayor por bombardeo con iones pesados'] },
  102:{ name: 'Nobelio',        description: 'El nobelio fue nombrado en honor de Alfred Nobel, inventor de la dinamita y fundador de los Premios Nobel. Su síntesis fue disputada durante años entre científicos soviéticos y estadounidenses. Es producido en cantidades de unos pocos átomos a la vez en aceleradores de iones pesados.', uses: ['Investigación del estado de oxidación +2 anómalo en actínidos y de la química en solución de actínidos pesados', 'Síntesis del laurencio (103) por bombardeo de nobelio con iones de helio en un ciclotón', 'Estudio de los límites de la tabla periódica y de los efectos relativistas en la química de los elementos superheavy'] },
  103:{ name: 'Laurencio',      description: 'El laurencio fue el último de los actínidos en ser sintetizado, en 1961, y recibe su nombre de Ernest Lawrence, inventor del ciclotrón. Cierra la serie de los actínidos y también es el elemento donde termina la serie 5f, por lo que marca el inicio de los metales de transición del período 7.', uses: ['Investigación de los actínidos más pesados y sus propiedades de configuración electrónica de la serie 5f terminal', 'Síntesis del rutherfordio (104) por bombardeo del laurencio con partículas alfa en experimentos nucleares', 'Determinación experimental de la configuración electrónica del laurencio mediante espectroscopía atómica'] },
  104:{ name: 'Rutherfordio',   description: 'El rutherfordio fue el primer elemento transactínido sintetizado. Fue nombrado en honor de Ernest Rutherford, padre del modelo nuclear atómico. Es el primer elemento del bloque d del período 7 y también el primer elemento cuya síntesis fue disputada oficialmente entre el JINR de Dubna y Lawrence Berkeley.', uses: ['Investigación de los elementos superpesados y de sus propiedades periódicas en la extensión de la tabla', 'Estudios de la química de los transactínidos y del efecto relativista en los elementos del período 7', 'Producción para estudios de semivida y propiedades nucleares en aceleradores de iones pesados'] },
  105:{ name: 'Dubnio',         description: 'El dubnio recibe su nombre definitivo de Dubna (Rusia), sede del JINR donde fue uno de los lugares de su descubrimiento. Fue durante mucho tiempo llamado "hahnio" por el grupo de Berkeley y "nielsbohrio" por el JINR antes de recibir un nombre oficial de la IUPAC en 1997.', uses: ['Experimentos de química en fase acuosa de elementos transactínidos a escala de pocos átomos', 'Estudio del comportamiento del grupo 5 de la tabla periódica en el período 7 por comparación con Nb y Ta', 'Investigación de los límites de producción nuclear y de las secciones de captura de fusión en aceleradores'] },
  106:{ name: 'Seaborgio',      description: 'El seaborgio fue nombrado en vida de Glenn Seaborg en 1994, convirtiéndolo en la única persona nombrada en un elemento durante su vida. Seaborg fue el descubridor de numerosos actínidos y actinoides. La IUPAC aceptó el nombre a pesar de su acalorada controversia por la rareza de esta distinción.', uses: ['Experimentos de química de elementos transactínidos en fase gaseosa a escala de unos pocos átomos', 'Estudio de la química del grupo 6 extendido más allá del tungsteno y el molibdeno', 'Investigación del efecto de los electrones relativistas sobre las propiedades de los elementos superheavy'] },
  107:{ name: 'Bohrio',         description: 'El bohrio fue nombrado en honor de Niels Bohr, el físico danes que estableció el modelo de Bohr del átomo. Fue sintetizado por primera vez en el JINR de Dubna en 1976. Muy pocos átomos de bohrio han sido producidos y todos los isótopos conocidos se desintegran en segundos o menos.', uses: ['Estudios de la semivida de los isótopos superpesados y cartografía de la carta de nódulos de isótopos del período 7', 'Verificación de las predicciones teóricas relativistas sobre la estructura electrónica del grupo 7 del período 7', 'Investigación de la "isla de estabilidad" de los elementos superpesados mediante síntesis sistemática'] },
  108:{ name: 'Hasio',          description: 'El hasio recibe su nombre del estado alemán de Hesse (en latín "Hassia"), donde se encuentra el GSI Helmholtz Centre for Heavy Ion Research en Darmstadt. Fue sintetizado en 1984. Sus isótopos tienen vidas medias muy cortas, del orden de segundos o menos. Ha sido objeto de experimentos químicos a escala atómica.', uses: ['Análisis de tetraóxido de hasio (HsO₄) gaseoso por analogía con el OsO₄ para predecir su química periódica', 'Estudio experimental de la química del grupo 8 (Fe, Ru, Os) extendido al período 7', 'Investigación de la tendencia de la intensidad de los efectos relativistas en los metales de transición del período 7'] },
  109:{ name: 'Meitnerio',      description: 'El meitnerio fue nombrado en honor de Lise Meitner, la física austriaco-sueca que contribuyó al descubrimiento de la fisión nuclear y fue excluida injustamente del Premio Nobel. Fue sintetizado en 1982 en el GSI de Darmstadt, Alemania. Tiene semividas de milisegundos o segundos.', uses: ['Investigación de las propiedades nucleares y la estructura de capas de los elementos superpesados del período 7', 'Reconocimiento histórico de la contribución científica de Lise Meitner a la física nuclear moderna', 'Mapa experimental de los isótopos extremos de neutrones en la región de los actínidos y transactínidos'] },
  110:{ name: 'Darmstadio',     description: 'El darmstadio fue sintetizado en 1994 en el GSI de Darmstadt, ciudad de la que recibe su nombre. Tienen semividas muy cortas, de microsegundos a milisegundos, y solo unos pocos átomos han sido producidos hasta la fecha. Se espera que sea un metal de transición del grupo 10 del período 7.', uses: ['Investigación de los elementos superpesados del grupo 10 (Ni, Pd, Pt) extendidos al período 7', 'Estudio de las predicciones relativistas que modifican profundamente la configuración electrónica esperada', 'Mapeo de los isótopos sintéticos superheavy y estudio de su radioactividad en física nuclear avanzada'] },
  111:{ name: 'Roentgenio',     description: 'El roentgenio fue sintetizado en 1994 en el GSI de Darmstadt y fue nombrado en honor de Wilhelm Conrad Röntgen, descubridor de los rayos X en 1895. Es un metal de transición del grupo 11 del período 7, el más pesado de su grupo junto al cobre, la plata y el oro.', uses: ['Investigación de las propiedades químicas del grupo 11 del período 7 extendido más allá del oro', 'Estudio del efecto relativista de contracción de la capa s (efecto relativista directo) más pronunciado que en el oro', 'Síntesis experimental y detección de isótopos con tiempo de vida medio en el rango de milisegundos'] },
  112:{ name: 'Copernicio',     description: 'El copernicio fue sintetizado en 1996 en el GSI de Darmstadt y nombrado en honor de Nicolás Copérnico, el astrónomo que propuso el modelo heliocéntrico del sistema solar. Los cálculos relativistas sugieren que podría comportarse como un gas noble en lugar de un metal, a diferencia del mercurio.', uses: ['Investigación de los efectos relativistas extremos en la química de los elementos del grupo 12 del período 7', 'Estudio del eventual comportamiento gaseoso del copernicio a temperatura ambiente por contracción relativista', 'Síntesis de isótopos de copernicio por fusión fría de iones de zinc sobre plomo en aceleradores especializados'] },
  113:{ name: 'Nihonio',        description: 'El nihonio fue sintetizado en 2004 por el RIKEN en Japón, de cuyo nombre japonés Nihon (日本) proviene el nombre del elemento. Fue el primer elemento descubierto en Asia. Su confirmación por la IUPAC en 2015 lo convirtió en el cuarto elemento del período 7 en ser nombrado oficialmente ese año.', uses: ['Investigación del grupo 13 del período 7 extendido más allá del talio y el indio', 'Estudio de la química del nihonio mediante comparación experimental con el talio usando técnicas radioquímicas', 'Inspiración nacional japonesa en ciencia y tecnología de frontera en física nuclear'] },
  114:{ name: 'Flerovio',       description: 'El flerovio fue nombrado en honor del Laboratorio de Reacciones Nucleares Flerov del JINR en Dubna, Rusia, donde fue sintetizado en 1999. Los cálculos relativistas predicen que podría comportarse más como un gas noble que como un metal pesado del grupo 14, debido a efectos de la contracción relativista.', uses: ['Investigación del grupo 14 del período 7 extendido más allá del plomo y el bismuto', 'Estudio experimental de los efectos relativistas que podrían hacer gaseoso al flerovio a temperatura ambiente', 'Mapa de los isótopos más cercanos a la hipotética "isla de estabilidad" de los elementos superpesados'] },
  115:{ name: 'Moscovio',       description: 'El moscovio fue sintetizado en 2003 en el JINR de Dubna (región de Moscú) y nombrado en honor de la región de Moscú. Es el segundo elemento cuya síntesis fue confirmada en el año 2016. Su síntesis implica la fusión de iones de calcio-48 con un blanco de americio-243.', uses: ['Investigación del grupo 15 del período 7 extendido más allá del bismuto y el ástato', 'Producción como blanco para la síntesis del tenneso (elemento 117) por desintegración alfa', 'Estudio de la estructura de capas nucleares y las semividas en la región de los elementos superpesados'] },
  116:{ name: 'Livermorio',     description: 'El livermorio fue sintetizado en 2000 en el JINR de Dubna en colaboración con el Lawrence Livermore National Laboratory (LLNL) de California, de cuya ciudad toma su nombre. Es el penúltimo elemento en ser oficialmente nombrado, junto al tenneso, en 2016.', uses: ['Investigación del grupo 16 del período 7 extendido más allá del polonio y el telurio', 'Producción como precursor del flerovio (114) por desintegración alfa en cadenas de desintegración observadas', 'Estudio de las propiedades nucleares de los isótopos de los elementos superpesados del período 7'] },
  117:{ name: 'Tenneso',        description: 'El tenneso fue sintetizado en 2010 en el JINR de Dubna con la colaboración del Oak Ridge National Laboratory (ORNL) y la Universidad de Vanderbilt, en Tennessee, de donde toma su nombre. Es el segundo halógeno más pesado conocido, aunque se espera que tenga propiedades muy diferentes al resto de los halógenos.', uses: ['Investigación del grupo 17 del período 7 extendido más allá del ástato y el yodo', 'Estudio de si la tendencia de electronegatividad de los halógenos se mantiene en el período 7 del bloque p', 'Producción de oganesón (118) como producto de la desintegración alfa del tenneso en cadenas de desintegración'] },
  118:{ name: 'Oganesón',       description: 'El oganesón es el elemento con mayor número atómico confirmado y el elemento más pesado de la tabla periódica. Fue sintetizado en 2002 en el JINR de Dubna y nombrado en honor del físico nuclear ruso Yuri Oganessian. Aunque debería ser un gas noble, los cálculos relativistas predicen que es un sólido a temperatura ambiente.', uses: ['Investigación de los efectos relativistas extremos que podrían hacer del oganesón un sólido pese a ser gas noble', 'Estudio del límite de la tabla periódica y de si el concepto de número atómico tiene límites físicos superiores', 'Síntesis futura de elementos de isla de estabilidad (elemento 120, 126 y más allá) basándose en las lecciones del oganesón'] },
};
